'use strict';


module.exports = arrayFlatten; function flattenWithDepth(array, result, depth) {for (var i = 0; i < array.length; i++) {var value = array[i];
    if (depth > 0 && Array.isArray(value)) {
      flattenWithDepth(value, result, depth - 1);
    } else {
      result.push(value);
    }
  }

  return result;
} function flattenForever(array, result) {for (var i = 0; i < array.length; i++) {var value = array[i];

    if (Array.isArray(value)) {
      flattenForever(value, result);
    } else {
      result.push(value);
    }
  }

  return result;
} function arrayFlatten(array, depth) {if (depth == null) {return flattenForever(array, []);
  }

  return flattenWithDepth(array, [], depth);
}